UPDATE LocalizedText SET Text = '{LOC_TRAIT_CIVILIZATION_LANDSOL_ALL_DESCRIPTION}{LOC_TRAIT_LEADER_AURORA_SANCTUARY_NAME}: {LOC_TRAIT_LEADER_AURORA_SANCTUARY_DESCRIPTION}' WHERE EXISTS (SELECT * FROM LocalizedText WHERE Tag = 'LOC_CIVILIZATION_VIETNAM_NAME') AND Tag = 'LOC_TRAIT_CIVILIZATION_LANDSOL_KOKKORO_PRINCESS_DESCRIPTION' AND Language = 'ko_KR';
